mapper.select:

SELECT COUNT(*)
FROM members
WHERE id = #{id}

SELECT *
FROM members
WHERE id = #{id}
AND passwd = #{passwd}

mapper.update:
UPDATE members
SET email = #{email}, phone = #{phone}, birth = #{birth}, sex = #{sex}, image= #{image}, major = #{major}
WHERE id = #{id}

mapper.select:
SELECT c.id, s.subject ,s.subnum
FROM course c, subject s 
WHERE c.subnum = s.subnum and c.id=#{id}
